import { AuthService } from './../../auth/services/auth.service';
import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Character } from 'src/app/characters.interface';

@Component({
  selector: 'app-detail',
  templateUrl: './detail.component.html',
  styleUrls: ['./detail.component.css']
})
export class DetailComponent {
  id: number = 0
  charactersByLocalStorage = JSON.parse(localStorage.getItem('characters')!)
  character!: Character

  constructor(private authService: AuthService, private router: Router) {
    this.router.params.subscribe((param) => {
      this.id = Number(param['id'])
      console.log(this.id)
      console.log(this.charactersByLocalStorage)
    })

    this.charactersByLocalStorage.filter((char: Character) => {
      if(char.id === this.id) {
        this.character = char
        console.log(this.character)
      }
    })
  }

  logout() {
    this.authService.logout().subscribe({
      next: (response) => {
        if(response === null) {
          sessionStorage.removeItem('token-characters')
          this.router.navigate(['login'])
        }
      },
      error: () => {

      }
    })
  }

}
